mapper.update:

UPDATE job_event SET stat = #{stat}, retry_count = #{retryCount}


, handler_agent_id = #{handlerAgentId} 


, handler_log_path = #{handlerLogPath}


 event_id = #{eventId}

mapper.select:
SELECT event_id, parent_event_id, job_id, parent_job_id, group_id, parent_group_id, executor_handlers, handler_type, cmd, params, param_file_path, stat, handler_agent_id, priority, retry_count, create_time, handler_log_path, dist_type, event_rule FROM job_event
stat = #{stat}
ORDER BY create_time DESC


SELECT count(*)
FROM job_event
WHERE stat = #{stat}

SELECT a.event_id, a.parent_event_id, a.job_id, a.parent_job_id, a.group_id, a.parent_group_id, a.executor_handlers, a.handler_type, a.cmd, a.params, a.param_file_path, a.stat, a.handler_agent_id, a.priority, a.retry_count, a.create_time, a.handler_log_path, a.dist_type, a.event_rule, b.job_name, b.admin, b.admin_phone, b.admin_email, b.handler_fail_strategy, b.crontab FROM job_event a, job_info b WHERE a.job_id=b.id
AND group_id = #{groupId}
AND event_id = #{eventId}
