mapper.update:
UPDATE job_scheduler
next_fire_time = #{nextFireTime}, running_status = #{runningStatus}, prev_fire_time = #{prevFireTime},
WHERE job_id = #{jobId}

mapper.select:
SELECT js.id, js.job_id, ji.job_name, ji.crontab, ji.create_time, js.running_status, js.nid, js.prev_fire_time, js.next_fire_time
FROM job_scheduler js LEFT JOIN job_info ji ON js.job_id=ji.id
WHERE js.running_status = #{runningStatus}

    and ji.user_id = #{userId}


    and ji.job_name like concat(#{jobName}, '%')

SELECT js.id, js.job_id, ji.job_name, ji.crontab, ji.create_time, js.running_status, js.nid, js.prev_fire_time, js.next_fire_time
FROM job_scheduler js, job_info ji
WHERE js.job_id = ji.id AND js.nid=#{nid}
